import { useState } from "react";
import { FaSearch, FaPlus, FaEdit, FaTrash } from "react-icons/fa";
import { Doughnut } from "react-chartjs-2";
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(ArcElement, Tooltip, Legend);

const FarmManager = () => {
  const [koiItems, setKoiItems] = useState([
    { id: 1, name: "Koi Fish A", quantity: 5, price: 100, image: "data:image/jpeg;base64," },
    { id: 2, name: "Koi Fish B", quantity: 10, price: 200, image: "data:image/jpeg;base64," },
    { id: 3, name: "Koi Fish C", quantity: 7, price: 150, image: "data:image/jpeg;base64," },
  ]);

  const [budget, setBudget] = useState({
    expenses: 5000,
    income: 10000,
    remaining: 5000,
  });

  const [searchTerm, setSearchTerm] = useState("");
  const [newKoi, setNewKoi] = useState({ name: "", quantity: 0, price: 0, image: "" });

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleAddKoi = () => {
    setKoiItems([...koiItems, { ...newKoi, id: koiItems.length + 1 }]);
    setNewKoi({ name: "", quantity: 0, price: 0, image: "" });
  };

  const handleUpdateBudget = (category, value) => {
    setBudget({ ...budget, [category]: parseFloat(value) });
  };

  const filteredKoi = koiItems.filter((koi) =>
    koi.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const chartData = {
    labels: ["Expenses", "Income", "Remaining"],
    datasets: [
      {
        data: [budget.expenses, budget.income, budget.remaining],
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  };

  return (
    <div className="container_farmmanager mx-auto p-4">
      <h1 className="text-3xl font-bold mb-8">Koi Fish Inventory and Budget Management</h1>
      
      {/* Inventory Section */}
      <section className="mb-12">
        <h2 className="text-2xl font-semibold mb-4">Koi Fish Inventory</h2>
        
        {/* Search and Add Koi */}
        <div className="flex flex-wrap mb-4">
          <div className="w-full md:w-1/2 mb-4 md:mb-0">
            <div className="relative">
              <input
                type="text"
                placeholder="Search koi..."
                className="w-full p-2 pl-10 border rounded"
                value={searchTerm}
                onChange={handleSearch}
              />
              <FaSearch className="absolute left-3 top-3 text-gray-400" />
            </div>
          </div>
          <div className="w-full md:w-1/2 flex space-x-2">
            <input
              type="text"
              placeholder="Koi name"
              className="flex-grow p-2 border rounded"
              value={newKoi.name}
              onChange={(e) => setNewKoi({ ...newKoi, name: e.target.value })}
            />
            <input
              type="number"
              placeholder="Quantity"
              className="w-20 p-2 border rounded"
              value={newKoi.quantity}
              onChange={(e) => setNewKoi({ ...newKoi, quantity: parseInt(e.target.value) })}
            />
            <input
              type="number"
              placeholder="Price"
              className="w-24 p-2 border rounded"
              value={newKoi.price}
              onChange={(e) => setNewKoi({ ...newKoi, price: parseFloat(e.target.value) })}
            />
            <button
              className="bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition duration-300"
              onClick={handleAddKoi}
            >
              <FaPlus />
            </button>
          </div>
        </div>
        
        {/* Koi Inventory Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredKoi.map((koi) => (
            <div key={koi.id} className="bg-white p-4 rounded shadow-md">
              <img src={koi.image} alt={koi.name} className="w-full h-40 object-cover mb-2 rounded" />
              <h3 className="font-semibold">{koi.name}</h3>
              <p>Quantity: {koi.quantity}</p>
              <p>Price: ${koi.price}</p>
              <div className="flex justify-end mt-2 space-x-2">
                <button className="text-blue-500 hover:text-blue-700">
                  <FaEdit />
                </button>
                <button className="text-red-500 hover:text-red-700">
                  <FaTrash />
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>
      
      {/* Budget Section */}
      <section>
        <h2 className="text-2xl font-semibold mb-4">Budget</h2>
        <div className="flex flex-wrap -mx-2">
          <div className="w-full md:w-1/2 px-2 mb-4">
            <div className="bg-white p-4 rounded shadow-md">
              <h3 className="font-semibold mb-2">Budget Breakdown</h3>
              <div className="space-y-2">
                {Object.entries(budget).map(([category, value]) => (
                  <div key={category} className="flex items-center">
                    <label className="w-24 capitalize">{category}:</label>
                    <input
                      type="number"
                      className="flex-grow p-1 border rounded"
                      value={value}
                      onChange={(e) => handleUpdateBudget(category, e.target.value)}
                    />
                  </div>
                ))}
              </div>
            </div>
          </div>
          <div className="w-full md:w-1/2 px-2 mb-4">
            <div className="bg-white p-4 rounded shadow-md">
              <h3 className="font-semibold mb-2">Budget Distribution</h3>
              <Doughnut data={chartData} />
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default FarmManager;
